var each, glob, misc;

module.exports = function(options, callback) {
  var ssh;
  options.log({
    message: "Entering remove",
    level: 'DEBUG',
    module: 'nikita/lib/system/remove'
  });
  // SSH connection
  ssh = this.ssh(options.ssh);
  if (options.argument != null) {
    // Validate parameters
    options.target = options.argument;
  }
  if (options.target == null) {
    options.target = options.source;
  }
  if (options.target == null) {
    return callback(Error("Missing option: \"target\""));
  }
  // Start real work
  return glob(ssh, options.target, function(err, files) {
    var status;
    if (err) {
      return callback(err);
    }
    status = false;
    return each(files).call(function(file, callback) {
      status = true;
      options.log({
        message: `Removing file ${file}`,
        level: 'INFO',
        module: 'nikita/lib/system/remove'
      });
      return misc.file.remove(ssh, file, callback);
    }).next(function(err) {
      return callback(err, status);
    });
  });
};

